mapper.select:

select

from finish_request finishr
where finishr.id = #{id,jdbcType=INTEGER}

SELECT
    EXISTS (
        SELECT 1 FROM finish_request WHERE member_id = #{memId} AND quest_id = #{questId}
    )

SELECT

FROM
    finish_request finishr


        finishr.quest_id = #{questId}


        AND finishr.member_id = #{memberId}


        AND finishr.username = #{username}


        AND finishr.created_time = #{createdTime}


        AND finishr.audit_time = #{auditTime}


        AND finishr.description = #{description}


        AND finishr.image_ids = #{imageIds}


        AND finishr.status = #{status}


        AND finishr.memo = #{memo}


        AND finishr.name = #{name}



ORDER BY
    finishr.created_time DESC

LIMIT
    #{pageNumber}, #{pageSize}

SELECT
    COUNT(*)
FROM
    finish_request finishr


        finishr.quest_id = #{questId}


        AND finishr.member_id = #{memberId}


        AND finishr.username = #{username}


        AND finishr.created_time = #{createdTime}


        AND finishr.audit_time = #{auditTime}


        AND finishr.description = #{description}


        AND finishr.image_ids = #{imageIds}


        AND finishr.status = #{status}


        AND finishr.memo = #{memo}


        AND finishr.name = #{name}
