mapper.select:
SELECT

FROM
    off_quest


        mem_id = #{memId,jdbcType=INTEGER}


        AND username = #{username,jdbcType=VARCHAR}


        AND title = #{title,jdbcType=VARCHAR}


        AND cate_id = #{cateId,jdbcType=INTEGER}


        AND cate_name = #{cateName,jdbcType=VARCHAR}


        AND award = #{award,jdbcType=DECIMAL}


        AND created_time = #{createdTime,jdbcType=TIMESTAMP}


        AND work_time = #{workTime,jdbcType=TIMESTAMP}


        AND work_place = #{workPlace,jdbcType=VARCHAR}


        AND description = #{description,jdbcType=VARCHAR}


        AND contact_phone = #{contactPhone,jdbcType=VARCHAR}


        AND status = #{status,jdbcType=VARCHAR}


        AND province_id = #{provinceId,jdbcType=INTEGER}


        AND city_id = #{cityId,jdbcType=INTEGER}


        AND region_id = #{regionId,jdbcType=INTEGER}



LIMIT
    #{pageNumber}, #{pageSize}

SELECT
    COUNT(*)
FROM
    off_quest


        mem_id = #{memId,jdbcType=INTEGER}


        AND username = #{username,jdbcType=VARCHAR}


        AND title = #{title,jdbcType=VARCHAR}


        AND cate_id = #{cateId,jdbcType=INTEGER}


        AND cate_name = #{cateName,jdbcType=VARCHAR}


        AND award = #{award,jdbcType=DECIMAL}


        AND created_time = #{createdTime,jdbcType=TIMESTAMP}


        AND work_time = #{workTime,jdbcType=TIMESTAMP}


        AND work_place = #{workPlace,jdbcType=VARCHAR}


        AND description = #{description,jdbcType=VARCHAR}


        AND contact_phone = #{contactPhone,jdbcType=VARCHAR}


        AND status = #{status,jdbcType=VARCHAR}


        AND province_id = #{provinceId,jdbcType=INTEGER}


        AND city_id = #{cityId,jdbcType=INTEGER}


        AND region_id = #{regionId,jdbcType=INTEGER}
