mapper.update:
update quest set title = #{title,jdbcType=VARCHAR}, created_time = #{createdTime,jdbcType=TIMESTAMP}, quest_cate_id = #{questCateId,jdbcType=INTEGER}, start_time = #{startTime,jdbcType=TIMESTAMP}, end_time = #{endTime,jdbcType=TIMESTAMP}, limit_time = #{limitTime,jdbcType=INTEGER}, total_amt = #{totalAmt,jdbcType=INTEGER}, left_amt = #{leftAmt,jdbcType=INTEGER}, award = #{award,jdbcType=DECIMAL}, contact_name = #{contactName,jdbcType=VARCHAR}, contact_phone = #{contactPhone,jdbcType=VARCHAR}, description = #{description,jdbcType=VARCHAR}, quest_detail = #{questDetail,jdbcType=VARCHAR}, url = #{url,jdbcType=VARCHAR}, memo = #{memo,jdbcType=VARCHAR}, offline = #{offline,jdbcType=BIT},
province_id = #{provinceId}, city_id = #{cityId}, region_id = #{regionId},
college_id = #{collegeId}, school_id = #{schoolId}, final_award = #{finalAward}, member_id = #{memberId} where id = #{id,jdbcType=INTEGER}

mapper.select:
SELECT

FROM
    quest qu


        qu.title = #{title}


        AND qu.created_time = #{createdTime}


        AND qu.quest_cate_id = #{questCateId}


        AND qu.start_time = #{startTime}


        AND qu.end_time = #{endTime}


        AND qu.limit_time = #{limitTime}


        AND qu.total_amt = #{totalAmt}


        AND qu.left_amt = #{leftAmt}


        AND qu.award = #{award}





        AND qu.award >= #{minAward} AND qu.award <= #{maxAward}




        AND qu.contact_name = #{contactName}


        AND qu.contact_phone = #{contactPhone}


        AND qu.description = #{description}


        AND qu.quest_detail = #{questDetail}


        AND qu.url = #{url}


        AND qu.memo = #{memo}


        ANd qu.offline = #{offline}




        AND qu.province_id = #{provinceId}


        AND qu.city_id = #{cityId}


        AND qu.region_id = #{regionId}


        AND qu.college_id = #{collegeId}


        AND qu.school_id = #{schoolId}



        AND qu.final_award = #{finalAward}



        AND qu.member_id = #{memberId}




ORDER BY
    qu.created_time DESC

LIMIT
  #{pageNumber}, #{pageSize}

SELECT
    COUNT(*)
FROM
    quest qu


        qu.title = #{title}


        AND qu.created_time = #{createdTime}


        AND qu.quest_cate_id = #{questCateId}


        AND qu.start_time = #{startTime}


        AND qu.end_time = #{endTime}


        AND qu.limit_time = #{limitTime}


        AND qu.total_amt = #{totalAmt}


        AND qu.left_amt = #{leftAmt}


