mapper.select:
SELECT
    ,



        ,


        ,


        ,

FROM
    recommended_post reco
LEFT JOIN
    member mem ON mem.id = reco.member_id

INNER JOIN
    job_post post ON post.id = reco.job_id


INNER JOIN
    second_hand_post post ON post.id = reco.sh_id


INNER JOIN
    resume re ON re.id = reco.resume_id




        AND reco.job_id = #{jobId,jdbcType=INTEGER}


        AND reco.sh_id = #{shId,jdbcType=INTEGER}


        AND reco.resume_id = #{resumeId,jdbcType=INTEGER}


        AND reco.apply_time = #{applyTime,jdbcType=TIMESTAMP}


        AND reco.validation = #{validation,jdbcType=BIT}


        AND reco.member_id = #{memberId,jdbcType=INTEGER}


        AND reco.memo = #{memo,jdbcType=VARCHAR}


        AND reco.order_index = #{orderIndex,jdbcType=INTEGER}


        AND reco.pass_time = #{passTime,jdbcType=TIMESTAMP}


        AND reco.remove_time = #{removeTime,jdbcType=TIMESTAMP}


ORDER BY
    reco.apply_time DESC
LIMIT
    #{pageNumber}, #{pageSize}

SELECT
    COUNT(*)
FROM
    recommended_post reco


        AND reco.job_id = #{jobId,jdbcType=INTEGER}


        AND reco.sh_id = #{shId,jdbcType=INTEGER}


        AND reco.resume_id = #{resumeId,jdbcType=INTEGER}


        AND reco.apply_time = #{applyTime,jdbcType=TIMESTAMP}


        AND reco.validation = #{validation,jdbcType=BIT}


        AND reco.member_id = #{memberId,jdbcType=INTEGER}


        AND reco.memo = #{memo,jdbcType=VARCHAR}


        AND reco.order_index = #{orderIndex,jdbcType=INTEGER}


        AND reco.pass_time = #{passTime,jdbcType=TIMESTAMP}


        AND reco.remove_time = #{removeTime,jdbcType=TIMESTAMP}

SELECT EXISTS (
    SELECT
        1
    FROM
        recommended_post reco
    WHERE
    reco.sh_id = #{shId}
    reco.job_id = #{jobId}
    reco.resume_id = #{resumeId}
)
